# Function to validate the passworddef password_check(passlst):    for passwd in passlst:        specialsym = ['$', '@', '#']        val = True        if len(passwd) < 6: #check length at least 6            val = False        if len(passwd) > 12: #chech max length            val = False        if not any(char.isdigit() for char in passwd): #check pass have at least one digit            val = False        if not any(char.isupper() for char in passwd): #check pass have at least one upper case alphbet             val = False        if not any(char.islower() for char in passwd): #check pass have at least one lower case alphbet            val = False        if not any(char in specialsym for char in passwd): #have at least one special symbol            val = False        if val:            print(passwd) #if full fil all condition then pront itp = input("Enter your password: ")passlst = [x for x in p.split(",")]password_check(passlst)