s = "New to Python or choosing between Python 2 and Python 3? Read Python 2 or Python 3."lst = [x for x in s.split(" ")] # store string in list and sort itlst.sort()freq = {}for i in lst: # To compute the frequency of the words from the input    if i in freq:        freq[i] += 1    else:        freq[i] = 1for n in freq:    print(f"{n} : {freq[n]}") # print the frequency which store in dictionary in kay value pair