# find a number which have all digit are even and print it in onelinedef findeven():    for a in range(1000, 3000+1):        lst = [int(i) for i in ' '.join(str(a)).split()]        lst2 = []        for x in lst:            if x % 2 == 0:                lst2.append(x)                if lst2 == lst:                    for b in lst2:                        for c in str(b):                            print(c, end="")                    print(end=",")findeven()